      const indexOfItemToremove = state.items.findIndex(item => item.id === action.payload);
      console.log('indexOfItemToremove', indexOfItemToremove);
      state.totalQuantity--;
      state.totalAmount -= state.items[indexOfItemToremove].price;

      if (indexOfItemToremove > -1) {
        console.log('state.items[indexOfItemToremove]', state.items[indexOfItemToremove]);
        state.items[indexOfItemToremove].quantity -= 1;
        state.items[indexOfItemToremove].total -= state.items[indexOfItemToremove].price;
      } else {
        console.log('inside else of indexOfItemToremove');
        state.items.splice(indexOfItemToremove, 1);
      }
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
