import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    totalQuantity: 0,
    totalAmount: 0,
  },
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.items.find(item => item.id === action.payload.id);
      state.totalQuantity++;
      state.totalAmount += action.payload.price;
      if (existingItem) {
        existingItem.quantity += 1;
        existingItem.total = existingItem.quantity * existingItem.price;
      } else {
        state.items.push({ ...action.payload, total: action.payload.price });
      }
    },
    removeFromCart: (state, action) => {
      console.log('action.payload', action.payload)
      const indexOfItemToremove = state.items.findIndex(item => item.id === action.payload);
      console.log('indexOfItemToremove', indexOfItemToremove);
      if (indexOfItemToremove) {
        console.log('state.items[indexOfItemToremove]', state.items[indexOfItemToremove]);
        state.items[indexOfItemToremove].quantity -= 1;
      }
      state.items.splice(indexOfItemToremove, 1);
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
